import React, { useState, useEffect } from 'react';

const Chatbot = () => {
  const [caloriesData, setCaloriesData] = useState(null);
  const [exerciseData, setExerciseData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Function to safely fetch JSON data
    const fetchData = async (url, setData, name) => {
      try {
        const response = await fetch(url);
        if (!response.ok) throw new Error(`Failed to load ${name}`);
        const data = await response.json();
        console.log(`✅ ${name} Loaded:`, data);
        setData(data);
      } catch (err) {
        console.error(`❌ Error loading ${name}:`, err);
        setError(err.message);
      }
    };

    fetchData("/data/calories.json", setCaloriesData, "Calories");
    fetchData("/data/exercise.json", setExerciseData, "Exercise");
  }, []);

  return (
    <div>
      <h2>Chatbot</h2>
      {error && <p style={{ color: "red" }}>⚠️ {error}</p>}
      <p>Calories: {caloriesData ? "Loaded ✅" : "Loading..."}</p>
      <p>Exercise: {exerciseData ? "Loaded ✅" : "Loading..."}</p>
    </div>
  );
};

export default Chatbot;
