import React, { Component } from 'react';
import { from } from 'rxjs';
import { map } from 'rxjs/operators';
import MacroNutrients from './components/macro-nutrients';
import MicroNutrients from './components/micro-nutrients';
import Foods from './components/foods';
import Chatbot from './components/Chatbot';
import initTutorial from './components/tutorial';
import { calculateMacroNutrients, calculateMicroNutrients } from './components/calculations';
import logo from './imgs/logo.png';
import foodData from './data/food.json';       // ✅ Importing food.json
import nutrientsData from './data/nutrients.json'; // ✅ Importing nutrients.json
import './App.css';

export default class App extends Component {
  state = {
    macroNutrients: null,
    microNutrients: null,
    food: foodData,            // ✅ Storing food data in state
    nutrients: nutrientsData,  // ✅ Storing nutrients data in state
  };

  componentDidMount() {
    console.log("✅ Food Data Loaded:", this.state.food);
    console.log("✅ Nutrients Data Loaded:", this.state.nutrients);
    initTutorial();
  }

  updateNutrients = selectedFoods$ => {
    this.setState({
      macroNutrients: calculateMacroNutrients(selectedFoods$),
      microNutrients: calculateMicroNutrients(selectedFoods$, this.state.nutrients),
    });
  };

  render() {
    return (
      <div className="App">
        <div className="leftPanel">
          <div className="logo">
            <img alt="Logo" src={logo} />
          </div>
          <Foods updateNutrients={this.updateNutrients} />
        </div>
        <div className="rightPanel">
          <MacroNutrients macroNutrients={this.state.macroNutrients} />
          <MicroNutrients definitions={this.state.nutrients} microNutrients={this.state.microNutrients} />
        </div>

        {/* ✅ Chatbot Component */}
        <Chatbot />
      </div>
    );
  }
}
