class ActionProvider {
  constructor(createChatBotMessage, setStateFunc) {
    this.createChatBotMessage = createChatBotMessage;
    this.setState = setStateFunc;
  }

  greet() {
    const message = this.createChatBotMessage("Hello! How can I assist you with food and nutrition?");
    this.updateChatbotState(message);
  }

  handleCaloriesQuery() {
    const message = this.createChatBotMessage("I can help you track calories! What food or activity would you like information on?");
    this.updateChatbotState(message);
  }

  handleNutritionInfo() {
    const message = this.createChatBotMessage("Nutrition is important! I can provide details on macronutrients, vitamins, and minerals. What do you need?");
    this.updateChatbotState(message);
  }

  handleUnknown() {
    const message = this.createChatBotMessage("I'm not sure how to respond to that. Could you rephrase?");
    this.updateChatbotState(message);
  }

  updateChatbotState(message) {
    this.setState((prev) => ({
      ...prev,
      messages: [...prev.messages, message],
    }));
  }
}

export default ActionProvider;
