class ActionProvider {
  constructor(createChatBotMessage, setStateFunc) {
    this.createChatBotMessage = createChatBotMessage;
    this.setState = setStateFunc;
  }

  greet() {
    const message = this.createChatBotMessage("Hello! How can I assist you today?");
    this.updateChatbotState(message);
  }

  handleCaloriesQuery() {
    const message = this.createChatBotMessage(
      "Your ideal calorie intake depends on factors like age, weight, activity level, and goals. Do you want a general guideline or a personalized suggestion?"
    );
    this.updateChatbotState(message);
  }

  handleUnknown() {
    const message = this.createChatBotMessage(
      "I'm not sure how to respond to that. Could you rephrase?"
    );
    this.updateChatbotState(message);
  }

  updateChatbotState(message) {
    this.setState((prev) => ({
      ...prev,
      messages: [...prev.messages, message],
    }));
  }
}

export default ActionProvider;
