import React, { useEffect, useState } from "react";
import MacroNutrients from "./components/macro-nutrients";
import MicroNutrients from "./components/micro-nutrients";
import Foods from "./components/foods";
import { calculateMacroNutrients, calculateMicroNutrients } from "./components/calculations";
import logo from "./imgs/logo.png";
import "./App.css";

function App() {
  const [caloriesData, setCaloriesData] = useState(null);
  const [exerciseData, setExerciseData] = useState(null);
  const [macroNutrients, setMacroNutrients] = useState(null);
  const [microNutrients, setMicroNutrients] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch calories.json
    fetch("/data/calories.json")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to load calories.json");
        }
        return response.json();
      })
      .then((data) => setCaloriesData(data))
      .catch((error) => setError(error.message));

    // Fetch exercise.json
    fetch("/data/exercise.json")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to load exercise.json");
        }
        return response.json();
      })
      .then((data) => setExerciseData(data))
      .catch((error) => setError(error.message));
  }, []);

  const updateNutrients = (selectedFoods) => {
    setMacroNutrients(calculateMacroNutrients(selectedFoods));
    setMicroNutrients(calculateMicroNutrients(selectedFoods));
  };

  return (
    <div className="App">
      <div className="leftPanel">
        <div className="logo">
          <img alt="Logo" src={logo} />
        </div>
        <Foods updateNutrients={updateNutrients} />
      </div>
      <div className="rightPanel">
        <h2>Calories Data</h2>
        {caloriesData ? <pre>{JSON.stringify(caloriesData, null, 2)}</pre> : <p>Loading...</p>}

        <h2>Exercise Data</h2>
        {exerciseData ? <pre>{JSON.stringify(exerciseData, null, 2)}</pre> : <p>Loading...</p>}

        <MacroNutrients macroNutrients={macroNutrients} />
        <MicroNutrients microNutrients={microNutrients} />
      </div>

      {error && <p style={{ color: "red" }}>Error: {error}</p>}
    </div>
  );
}

export default App;
