class MessageParser {
  constructor(actionProvider) {
    this.actionProvider = actionProvider;
  }

  parse(message) {
    const lowerCaseMessage = message.toLowerCase();

    // Check for calorie-related queries
    if (lowerCaseMessage.includes("calorie") || lowerCaseMessage.includes("calories")) {
      const weightMatch = lowerCaseMessage.match(/\b\d+\b/); // Extract number (weight)
      if (weightMatch) {
        const weight = parseInt(weightMatch[0]); // Convert to integer
        this.actionProvider.handleCalorieQuery(weight);
      } else {
        this.actionProvider.handleUnknown();
      }
      return;
    }

    this.actionProvider.handleUnknown();
  }
}

export default MessageParser;
