import React, { useState, useEffect } from "react";
import caloriesDataJson from "../data/calories.json";
import exerciseDataJson from "../data/exercise.json";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [caloriesData, setCaloriesData] = useState([]);
  const [exerciseData, setExerciseData] = useState([]);

  // Load JSON data on component mount
  useEffect(() => {
    setCaloriesData(caloriesDataJson);
    setExerciseData(exerciseDataJson);
    console.log("✅ Calories Data Loaded:", caloriesDataJson.length);
    console.log("✅ Exercise Data Loaded:", exerciseDataJson.length);
  }, []);

  const generateResponse = (userMessage) => {
    if (!caloriesData.length || !exerciseData.length) {
      return "Data is still loading. Please try again.";
    }

    const lowerCaseMessage = userMessage.toLowerCase();
    
    // Find food item
    const foodItem = caloriesData.find(
      (item) => item.name?.toLowerCase() === lowerCaseMessage
    );

    // Find exercise item
    const exerciseItem = exerciseData.find(
      (item) => item.name?.toLowerCase() === lowerCaseMessage
    );

    console.log("User Input:", userMessage);
    console.log("Food Item Found:", foodItem);
    console.log("Exercise Item Found:", exerciseItem);

    if (foodItem) {
      return `🍏 A ${foodItem.name} contains ${foodItem.calories} calories.`;
    } else if (exerciseItem) {
      return `🏃 Doing ${exerciseItem.name} burns ${exerciseItem.calories_burned} calories.`;
    } else {
      return "❌ Sorry, I couldn't find that information. Try asking about food or exercise.";
    }
  };

  const handleSendMessage = () => {
    if (!input.trim()) return;

    const userMessage = input.trim();
    const botResponse = generateResponse(userMessage);

    setMessages([...messages, { text: userMessage, sender: "user" }, { text: botResponse, sender: "bot" }]);
    setInput("");
  };

  return (
    <div className="chatbot">
      <div className="chatbox">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="input-box">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me about food or exercise..."
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
