class MessageParser {
  constructor(actionProvider) {
    this.actionProvider = actionProvider;
  }

  parse(message) {
    const lowerCaseMessage = message.toLowerCase();

    // Extract weight
    const weightMatch = lowerCaseMessage.match(/(\d+)\s*kg/); // Looks for "50 kg"
    const weight = weightMatch ? parseInt(weightMatch[1]) : null;

    // Extract activity (basic detection)
    const activityMatch = lowerCaseMessage.match(/(running|walking|cycling|exercise|workout)/);
    const activity = activityMatch ? activityMatch[1] : null;

    // Extract duration
    const durationMatch = lowerCaseMessage.match(/(\d+)\s*(minutes|min)/);
    const duration = durationMatch ? parseInt(durationMatch[1]) : null;

    // Calorie intake query
    if (lowerCaseMessage.includes("calorie") || lowerCaseMessage.includes("calories")) {
      if (weight) {
        this.actionProvider.handleCalorieQuery({ weight });
      } else {
        this.actionProvider.handleUnknown();
      }
      return;
    }

    // Calorie burn query
    if (activity && duration) {
      if (weight) {
        this.actionProvider.handleExerciseQuery({ weight, duration, activity });
      } else {
        this.actionProvider.handleUnknown();
      }
      return;
    }

    // Default response
    this.actionProvider.handleUnknown();
  }
}

export default MessageParser;
