class ActionProvider {
  constructor(createChatBotMessage, setStateFunc) {
    this.createChatBotMessage = createChatBotMessage;
    this.setState = setStateFunc;
  }

  async handleCalorieQuery(weight) {
    console.log("Received weight:", weight);

    try {
      // Fetch JSON files from the public folder
      const caloriesResponse = await fetch(process.env.PUBLIC_URL + "/data/calories.json");
      const exerciseResponse = await fetch(process.env.PUBLIC_URL + "/data/exercise.json");

      if (!caloriesResponse.ok || !exerciseResponse.ok) {
        throw new Error("Failed to load JSON data");
      }

      const caloriesData = await caloriesResponse.json();
      const exerciseData = await exerciseResponse.json();

      console.log("Exercise Data:", exerciseData);
      console.log("Calorie Data:", caloriesData);

      // Find user data based on weight
      const user = exerciseData.find(entry => parseFloat(entry.Weight) === parseFloat(weight));

      let message;
      if (user) {
        const userCalories = caloriesData.find(entry => entry.User_ID === user.User_ID);
        if (userCalories) {
          message = this.createChatBotMessage(
            `Based on our data, a person weighing ${weight} kg (User ID: ${user.User_ID}) burned around ${userCalories.Calories} kcal.`
          );
        } else {
          message = this.createChatBotMessage(
            "I couldn't find exact calorie data for your weight. Please provide more details like age and activity level!"
          );
        }
      } else {
        message = this.createChatBotMessage(
          "Sorry, I couldn't find any matching user data for this weight."
        );
      }

      this.setState(prev => ({
        ...prev,
        messages: [...prev.messages, message],
      }));
    } catch (error) {
      console.error("Error loading JSON files:", error);
      const errorMessage = this.createChatBotMessage("Error loading data. Please try again later.");
      this.setState(prev => ({
        ...prev,
        messages: [...prev.messages, errorMessage],
      }));
    }
  }

  async handleExerciseQuery({ weight, duration, activity }) {
    console.log(`Activity Query -> Weight: ${weight}, Activity: ${activity}, Duration: ${duration} minutes`);

    try {
      const exerciseResponse = await fetch(process.env.PUBLIC_URL + "/data/exercise.json");

      if (!exerciseResponse.ok) {
        throw new Error("Failed to load exercise data");
      }

      const exerciseData = await exerciseResponse.json();
      const user = exerciseData.find(entry => parseFloat(entry.Weight) === parseFloat(weight));

      let message;
      if (user && user[activity]) {
        const caloriesBurned = (user[activity] / 60) * duration;
        message = this.createChatBotMessage(
          `A ${weight} kg person burns approximately ${caloriesBurned.toFixed(2)} kcal by ${activity} for ${duration} minutes.`
        );
      } else {
        message = this.createChatBotMessage(
          `Sorry, I couldn't find data for ${activity} at your weight. Please provide more details.`
        );
      }

      this.setState(prev => ({
        ...prev,
        messages: [...prev.messages, message],
      }));
    } catch (error) {
      console.error("Error loading exercise data:", error);
      const errorMessage = this.createChatBotMessage("Error loading data. Please try again later.");
      this.setState(prev => ({
        ...prev,
        messages: [...prev.messages, errorMessage],
      }));
    }
  }

  handleUnknown() {
    const message = this.createChatBotMessage(
      "I'm sorry, I didn't understand that. Could you rephrase your question or ask about calories, food nutrition, or exercise?"
    );

    this.setState(prev => ({
      ...prev,
      messages: [...prev.messages, message],
    }));
  }
}

export default ActionProvider;
