import React, { useState, useEffect } from 'react';

const Chatbot = () => {
  const [caloriesData, setCaloriesData] = useState(null);
  const [exerciseData, setExerciseData] = useState(null);

  useEffect(() => {
    // Fetch calories.json
    fetch("/data/calories.json")
      .then(response => {
        if (!response.ok) throw new Error("Failed to load calories.json");
        return response.json();
      })
      .then(data => {
        console.log("✅ Calories Data Loaded:", data);
        setCaloriesData(data);
      })
      .catch(error => console.error("❌ Error loading calories.json:", error));

    // Fetch exercise.json
    fetch("/data/exercise.json")
      .then(response => {
        if (!response.ok) throw new Error("Failed to load exercise.json");
        return response.json();
      })
      .then(data => {
        console.log("✅ Exercise Data Loaded:", data);
        setExerciseData(data);
      })
      .catch(error => console.error("❌ Error loading exercise.json:", error));
  }, []);

  return (
    <div>
      <h2>Chatbot</h2>
      <p>Calories: {caloriesData ? "Loaded ✅" : "Loading..."}</p>
      <p>Exercise: {exerciseData ? "Loaded ✅" : "Loading..."}</p>
    </div>
  );
};

export default Chatbot;
