import React, { useState, useEffect } from 'react';

const Chatbot = () => {
  const [caloriesData, setCaloriesData] = useState(null);
  const [exerciseData, setExerciseData] = useState(null);
  const [error, setError] = useState(null);
  const [userInput, setUserInput] = useState('');
  const [chatResponse, setChatResponse] = useState('Ask me about food or exercise!');

  useEffect(() => {
    // Fetch calories.json
    fetch('/data/calories.json')
      .then((response) => {
        if (!response.ok) throw new Error('Failed to load calories.json');
        return response.json();
      })
      .then((data) => setCaloriesData(data))
      .catch((err) => setError(`Error loading calories.json: ${err.message}`));

    // Fetch exercise.json
    fetch('/data/exercise.json')
      .then((response) => {
        if (!response.ok) throw new Error('Failed to load exercise.json');
        return response.json();
      })
      .then((data) => setExerciseData(data))
      .catch((err) => setError(`Error loading exercise.json: ${err.message}`));
  }, []);

  // Function to handle user queries
  const handleUserQuery = () => {
    if (!userInput.trim()) return;

    let response = "I'm sorry, I couldn't find that information. Ask me about food or exercise.";

    // Search in calories data
    if (caloriesData) {
      const food = caloriesData.find((item) =>
        item.name.toLowerCase() === userInput.toLowerCase()
      );
      if (food) {
        response = `üçè ${food.name} contains ${food.calories} calories per ${food.quantity}.`;
      }
    }

    // Search in exercise data
    if (exerciseData) {
      const exercise = exerciseData.find((item) =>
        item.name.toLowerCase() === userInput.toLowerCase()
      );
      if (exercise) {
        response = `üèãÔ∏è ${exercise.name} burns ${exercise.calories_burned} calories in ${exercise.duration} minutes.`;
      }
    }

    setChatResponse(response);
    setUserInput('');
  };

  return (
    <div className="chatbot">
      <h2>Chatbot</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      <p><strong>Chatbot:</strong> {chatResponse}</p>
      
      <input
        type="text"
        placeholder="Ask about food or exercise..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />
      <button onClick={handleUserQuery}>Ask</button>
    </div>
  );
};

export default Chatbot;
