import React, { Component } from "react";
import MacroNutrients from "./components/macro-nutrients";
import MicroNutrients from "./components/micro-nutrients";
import ChatbotComponent from "./components/chatbot/Chatbot";

import initTutorial from "./components/tutorial";
import Foods from "./components/foods";
import { calculateMacroNutrients, calculateMicroNutrients } from "./components/calculations";

import logo from "./imgs/logo.png";

// Importing styles
import "./App.css";

export default class App extends Component {
  state = {
    caloriesData: null,
    exerciseData: null,
    macroNutrients: null,
    microNutrients: null,
  };

  // Removed nutrients.json references ✅

  updateNutrients = (selectedFoods$) => {
    this.setState({
      macroNutrients: calculateMacroNutrients(selectedFoods$),
      microNutrients: calculateMicroNutrients(selectedFoods$), // Removed extra params ✅
    });
  };

  async componentDidMount() {
    initTutorial(); // Keep this if needed

    try {
      const caloriesResponse = await fetch(process.env.PUBLIC_URL + "/data/calories.json");
      const exerciseResponse = await fetch(process.env.PUBLIC_URL + "/data/exercise.json");

      if (!caloriesResponse.ok || !exerciseResponse.ok) {
        throw new Error("Failed to load JSON files");
      }

      const caloriesData = await caloriesResponse.json();
      const exerciseData = await exerciseResponse.json();

      console.log("Calories Data:", caloriesData);
      console.log("Exercise Data:", exerciseData);

      this.setState({ caloriesData, exerciseData });
    } catch (error) {
      console.error("Error fetching JSON:", error);
    }
  }

  render() {
    return (
      <div className="App">
        <div className="leftPanel">
          <div className="logo">
            <img alt="Logo" src={logo} />
          </div>
          <Foods updateNutrients={this.updateNutrients} />
        </div>
        <div className="rightPanel">
          <MacroNutrients macroNutrients={this.state.macroNutrients} />
          <MicroNutrients microNutrients={this.state.microNutrients} /> 
          {/* Removed nutrients.json dependency ✅ */}
        </div>
        {/* Adding the Chatbot component */}
        <ChatbotComponent />
      </div>
    );
  }
}
