import React, { useState, useEffect } from "react";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [caloriesData, setCaloriesData] = useState([]);
  const [exerciseData, setExerciseData] = useState([]);

  // Load JSON Data when component mounts
  useEffect(() => {
    fetch("/data/calories.json")
      .then((response) => response.json())
      .then((data) => {
        console.log("Calories Data Loaded:", data);
        setCaloriesData(data);
      })
      .catch((error) => console.error("Error loading calories data:", error));

    fetch("/data/exercise.json")
      .then((response) => response.json())
      .then((data) => {
        console.log("Exercise Data Loaded:", data);
        setExerciseData(data);
      })
      .catch((error) => console.error("Error loading exercise data:", error));
  }, []);

  // Function to generate chatbot response
  const generateResponse = (userMessage) => {
    const lowerCaseMessage = userMessage.toLowerCase();
    
    const foodItem = caloriesData.find(item => item.name.toLowerCase() === lowerCaseMessage);
    const exerciseItem = exerciseData.find(item => item.name.toLowerCase() === lowerCaseMessage);
    
    if (foodItem) {
      return `A ${foodItem.name} contains ${foodItem.calories} calories.`;
    } else if (exerciseItem) {
      return `Doing ${exerciseItem.name} burns ${exerciseItem.calories_burned} calories.`;
    } else {
      return "I'm sorry, I couldn't find that information. Ask me about food or exercise...";
    }
  };
  
  // Handle user message send
  const handleSendMessage = () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    const botMessage = { text: generateResponse(input), sender: "bot" };

    setMessages([...messages, userMessage, botMessage]);
    setInput(""); // Clear input field
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          placeholder="Ask me about food or exercise..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
